mapper.select:
SELECT rnum,EDU_BOARD_NO, EDU_BOARD_TITLE, EDU_BOARD_START_PERIOD, EDU_BOARD_MEMBER_COUNT, EDU_BOARD_CATEGORY, EDU_BOARD_CONTENT, EDU_BOARD_STATUS FROM (SELECT row_number() over(ORDER BY EDU_BOARD_NO DESC) as rnum,EDU_BOARD_NO, EDU_BOARD_TITLE, EDU_BOARD_STATUS, EDU_BOARD_START_PERIOD, EDU_BOARD_MEMBER_COUNT, EDU_BOARD_CATEGORY, EDU_BOARD_CONTENT FROM edu_apply_board
edu_board_status =#{status}
) WHERE rnum BETWEEN #{pagination.startRowNumber} and #{pagination.endRowNumber}

SELECT r.rnum, e.EDU_BOARD_NO, e.EDU_BOARD_TITLE, e.EDU_BOARD_ADDRESS, r.EDU_BOARD_STATUS, r.TOTAL_COUNT FROM EDU_APPLY_BOARD e INNER JOIN (SELECT row_number() over(ORDER BY EDU_BOARD_NO DESC) as rnum, EDU_BOARD_NO, EDU_BOARD_STATUS, COUNT(EDU_BOARD_NO) AS TOTAL_COUNT FROM EDU_BOARD_MEMBER_COUNT
EDU_BOARD_STATUS = #{status}
GROUP BY EDU_BOARD_NO, EDU_BOARD_STATUS ) r ON e.EDU_BOARD_NO = r.EDU_BOARD_NO WHERE rnum BETWEEN #{pagination.startRowNumber} and #{pagination.endRowNumber}
